options.customCss:
:root {
  /* Neon Brand Colors */
  --chat--pink: #ff4d8d;
  --chat--purple: #7c5cff;
  --chat--cyan: #2df3ff;
  --chat--green: #22e6a7;

  /* Gradients */
  --chat--gradient-primary: linear-gradient(135deg, #ff4d8d, #7c5cff);
  --chat--gradient-secondary: linear-gradient(135deg, #22e6a7, #2df3ff);
  --chat--gradient-dark: linear-gradient(135deg, #0f172a, #020617);

  /* Base Colors */
  --chat--color-light: #f8fafc;
  --chat--color-dark: #020617;
  --chat--glass: rgba(255, 255, 255, 0.12);

  /* Glow */
  --chat--glow-primary: 0 0 25px rgba(255, 77, 141, 0.45);
  --chat--glow-secondary: 0 0 25px rgba(34, 230, 167, 0.45);

  /* Typography */
  --chat--font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;

  /* Layout */
  --chat--spacing: 1rem;
  --chat--border-radius: 1.4rem;
  --chat--transition: 0.25s ease;

  /* Window */
  --chat--window--width: 420px;
  --chat--window--height: 640px;
  --chat--window--z-index: 9999;
}

/* ============================= */
/* ü™ü CHAT WINDOW */
/* ============================= */

.chat-window {
  width: var(--chat--window--width);
  height: var(--chat--window--height);
  display: flex;
  flex-direction: column;
  background: linear-gradient(
    180deg,
    rgba(15, 23, 42, 0.92),
    rgba(2, 6, 23, 0.97)
  );
  backdrop-filter: blur(18px);
  border-radius: 1.5rem;
  box-shadow:
    0 30px 80px rgba(0, 0, 0, 0.7),
    inset 0 0 0 1px rgba(255, 255, 255, 0.06);
  overflow: hidden;
  font-family: var(--chat--font-family);
}

/* ============================= */
/* üß† HEADER */
/* ============================= */

.chat-header {
  background: var(--chat--gradient-primary);
  color: white;
  padding: 1.2rem;
  position: relative;
  overflow: hidden;
}

.chat-header::after {
  content: '';
  position: absolute;
  inset: 0;
  background: linear-gradient(
    120deg,
    transparent,
    rgba(255, 255, 255, 0.25),
    transparent
  );
  animation: shimmer 5s linear infinite;
}

@keyframes shimmer {
  from { transform: translateX(-100%); }
  to   { transform: translateX(100%); }
}

/* ============================= */
/* üí¨ MESSAGES */
/* ============================= */

.chat-messages {
  flex: 1;
  padding: 1.25rem;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
}

.chat-message {
  max-width: 70%;
  padding: 0.9rem 1.2rem;
  border-radius: 1.4rem;
  font-size: 0.95rem;
  line-height: 1.6;
  animation: pop 0.25s ease-out;
}

/* BOT MESSAGE */
.chat-message.bot {
  align-self: flex-start;
  background: rgba(255, 255, 255, 0.9);
  color: #020617;
  box-shadow: 0 10px 30px rgba(255, 255, 255, 0.15);
  border-bottom-left-radius: 0.4rem;
}

/* USER MESSAGE */
.chat-message.user {
  align-self: flex-end;
  background: var(--chat--gradient-secondary);
  color: #020617;
  box-shadow: var(--chat--glow-secondary);
  border-bottom-right-radius: 0.4rem;
}

@keyframes pop {
  from {
    opacity: 0;
    transform: translateY(8px) scale(0.95);
  }
  to {
    opacity: 1;
    transform: translateY(0) scale(1);
  }
}

/* ============================= */
/* ‚å®Ô∏è TYPING INDICATOR */
/* ============================= */

.chat-typing {
  display: flex;
  gap: 6px;
  padding: 0.5rem 1rem;
}

.chat-typing span {
  width: 6px;
  height: 6px;
  border-radius: 50%;
  animation: typing 1.4s infinite ease-in-out;
}

.chat-typing span:nth-child(1) {
  background: var(--chat--pink);
}

.chat-typing span:nth-child(2) {
  background: var(--chat--purple);
  animation-delay: 0.2s;
}

.chat-typing span:nth-child(3) {
  background: var(--chat--cyan);
  animation-delay: 0.4s;
}

@keyframes typing {
  0%, 80%, 100% {
    transform: scale(0.3);
    opacity: 0.3;
  }
  40% {
    transform: scale(1);
    opacity: 1;
  }
}

/* ============================= */
/* ‚úçÔ∏è INPUT AREA */
/* ============================= */

.chat-input {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin: 0.75rem;
  padding: 0.5rem;
  background: var(--chat--glass);
  border-radius: 999px;
  backdrop-filter: blur(12px);
  box-shadow:
    inset 0 0 0 1px rgba(255, 255, 255, 0.15),
    0 8px 24px rgba(0, 0, 0, 0.4);
}

.chat-input textarea {
  flex: 1;
  resize: none;
  border: none;
  background: transparent;
  color: white;
  padding: 0.6rem 1rem;
  font-family: var(--chat--font-family);
  font-size: 0.95rem;
  outline: none;
}

/* ============================= */
/* üöÄ SEND BUTTON */
/* ============================= */

.chat-send {
  width: 44px;
  height: 44px;
  border-radius: 50%;
  border: none;
  background: var(--chat--gradient-primary);
  color: white;
  cursor: pointer;
  box-shadow: var(--chat--glow-primary);
  transition: transform var(--chat--transition);
  animation: pulse 2.5s infinite;
}

.chat-send:hover {
  transform: scale(1.1);
}

@keyframes pulse {
  0%   { box-shadow: 0 0 0 0 rgba(255, 77, 141, 0.6); }
  70%  { box-shadow: 0 0 0 18px rgba(255, 77, 141, 0); }
  100% { box-shadow: 0 0 0 0 rgba(255, 77, 141, 0); }
}

/* ============================= */
/* üü£ FLOATING TOGGLE BUTTON */
/* ============================= */

.chat-toggle {
  width: 60px;
  height: 60px;
  border-radius: 50%;
  background: var(--chat--gradient-primary);
  box-shadow:
    var(--chat--glow-primary),
    0 20px 40px rgba(0, 0, 0, 0.6);
  cursor: pointer;
  transition: transform 0.25s ease;
}

.chat-toggle:hover {
  transform: scale(1.1);
}

